const themeToggle=document.getElementById('theme-toggle'),body=document.body,icon=themeToggle.querySelector('i'),prefersDark=window.matchMedia('(prefers-color-scheme: dark)').matches;prefersDark?(body.classList.add('dark-mode'),icon.className='fa fa-sun-o',themeToggle.setAttribute('aria-label','Switch to light mode'),themeToggle.setAttribute('aria-pressed','true')):(body.classList.add('light-mode'),icon.className='fa fa-moon-o',themeToggle.setAttribute('aria-label','Switch to dark mode'),themeToggle.setAttribute('aria-pressed','false'));themeToggle.addEventListener('click',()=>{body.classList.contains('dark-mode')?(body.classList.remove('dark-mode'),body.classList.add('light-mode'),icon.className='fa fa-moon-o',themeToggle.setAttribute('aria-label','Switch to dark mode'),themeToggle.setAttribute('aria-pressed','false')):(body.classList.remove('light-mode'),body.classList.add('dark-mode'),icon.className='fa fa-sun-o',themeToggle.setAttribute('aria-label','Switch to light mode'),themeToggle.setAttribute('aria-pressed','true'))});
